/* eslint-disable no-underscore-dangle */
import React, { useEffect, useContext } from 'react';
import { Box, Grid, Container } from '@material-ui/core';
import { withStyles } from '@material-ui/core/styles';
import PropTypes from 'prop-types';
// import CloseIcon from '@material-ui/icons/Close';
// import AddressCard from '../../components/addressCard';
import Text from '../../components/text';
import Button from '../../components/button';
// import Location from '../../components/location';
import { getAddress } from '../../lib/services/address';
import { GlobalContext } from '../../../pages/_app';
import { CLOSE_MAP, ADDRESS_LIST, PURCHASE_DATA } from '../../lib/utility/type';

const styles = (theme) => ({
  root: {
    marginTop: 20,
  },
  addressWrapper: {
    display: 'flex',
    flexDirection: 'row',
    flexWrap: 'wrap',
    // alignItems : "center",
    // justifyContent : "center",
    width: '100%',
    height: 600,
    gap: 14,
    boxShadow: '0 0 6px 0 rgba(0, 0, 0, 0.1)',
    borderRadius: 10,
    padding: 30,
    marginTop: 20,
    marginBottom: 20,
    overflowY: 'scroll',
    [theme.breakpoints.down('md')]: {
      height: 300,
      overflowX: 'scroll',
    },
    [theme.breakpoints.up('md')]: {
      height: 600,
      overflowY: 'scroll',
    },
  },
  calculationWrapper: {
    width: '80%',
    height: 600,
    boxShadow: '0 0 6px 0 rgba(0, 0, 0, 0.1)',
    borderRadius: 10,
    marginTop: 20,
    marginBottom: 20,
    paddingLeft: 20,
    paddingRight: 20,
    paddingTop: 20,
    paddingBottom: 20,
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    [theme.breakpoints.between('sm', 'md')]: {
      width: '100%',
    },
    [theme.breakpoints.down('sm')]: {
      width: '100%',
    },
    [theme.breakpoints.up('md')]: {
      width: '80%',
    },
  },
  calculationBox: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'flex-end',
  },
  priceWrapper: {
    display: 'flex',
    flexDirection: 'row',
    marginBottom: 10,
  },
  serviceDescription: {
    display: 'flex',
    flexDirection: 'column',
    // borderBottom : "1px solid rgba(0,0,0,0.3) ",
    width: '100%',
    // paddingBottom : 5
  },
  finalPrice: {
    display: 'flex',
    flexDirection: 'column',
    width: '80%',
  },
  serviceLeft: {
    width: '10%',
  },
  serviceRight: {
    width: '10%',
  },
  button: {
    width: 'auto',
    paddingLeft: 10,
    paddingRight: 10,
  },
  upper: {
    height: '100%',
    overflowY: 'scroll',
  },
  lower: {
    height: '30%',
  },
  title: {
    marginBottom: 40,
  },
});

const Order = ({ classes, query }) => {
  const globalContext = useContext(GlobalContext);
  const [purchaseData, setPurchaseData] = React.useState(null);

  useEffect(() => {
    const tempVal = JSON.parse(query.purchaseData);
    setPurchaseData(tempVal);
    getAddress().then((addressList) => {
      const tempArr = [];
      addressList.data.map((adderData) => {
        const aData = { ...adderData };
        // aData.selected = false;
        return tempArr.push(aData);
      });
      globalContext.allDispatch({
        type: ADDRESS_LIST,
        value: tempArr,
      });
    });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const addAddress = () => {
    // setOpen(true)
    globalContext.allDispatch({
      type: CLOSE_MAP,
      value: true,
    });
  };

  const handleClose = () => {
    // setOpen(false)
    globalContext.allDispatch({
      type: CLOSE_MAP,
      value: false,
    });
  };

  const onPayNow = () => {};

  const onCashOnDelivery = () => {};

  const onRemoveItem = (id) => {
    const serviceList = { ...purchaseData };
    purchaseData.service_list.map((data, index) => {
      if (data.id === id) {
        serviceList.service_list.splice(index, 1);
      }
      return data;
    });
    globalContext.allDispatch({
      type: PURCHASE_DATA,
      value: serviceList,
    });
    setPurchaseData(serviceList);
  };

  return <Box className={classes.root} />;
};

Order.propTypes = {
  query: PropTypes.object,
};

export default withStyles(styles)(Order);
