import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import { Box, IconButton } from '@material-ui/core';
import PropTypes from 'prop-types';
import Text from '../text';

const styles = (theme) => ({
  root: {
    display: 'flex',
    flexDirection: 'column',
    overflow: 'hidden',
  },
  headerWrapper: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between',
    overflow: 'hidden',
  },
  subWrapper: {
    display: 'flex',
    flexDirection: 'column',
  },
  arrowWrapper: {
    display: 'flex',
    flexDirection: 'row',
    width: 120,
  },
  leftArrow: {},
  commonTStyle: {
    margin: 0,
    lineHeight: 1,
  },
  scrollPad: {
    paddingLeft: 5,
    paddingRight: 5,
    display: 'flex',
  },
  previous: {
    height: 45,
    width: 45,
    borderRadius: '50%',
    backgroundImage: 'url(/static/images/right_arrow.png)',
    backgroundSize: '50%',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    backgroundColor: '#B8B8B8',
    boxShadow: '1px 3px 13px rgba(0, 0, 0, 0.16)',
    transform: 'rotate(180deg)',
  },
  next: {
    height: 45,
    width: 45,
    borderRadius: '50%',
    backgroundImage: 'url(/static/images/right_arrow.png)',
    backgroundSize: '50%',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    backgroundColor: '#B8B8B8',
    boxShadow: '1px 3px 13px rgba(0, 0, 0, 0.16)',
  },
  navStyle: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between',
    // width: 65,
    [theme.breakpoints.down(767)]: {
      display: 'none',
    },
    [theme.breakpoints.up(768)]: {
      display: 'flex',
    },
  },
  hiddenNavStyle: {
    padding: 15,
    display: 'none',
    [theme.breakpoints.down(767)]: {
      display: 'block',
    },
    [theme.breakpoints.up(768)]: {
      display: 'none',
    },
  },
  iconButton: {
    padding: 0,
    marginLeft: 20,
    [theme.breakpoints.down(768)]: {
      marginLeft: 0,
    },
    [theme.breakpoints.up(768)]: {
      marginLeft: 20,
    },
  },
});

const Carousel = ({
  classes,
  title,
  subTitle,
  children,
  previousDiv,
  nextDiv,
}) => (
  <Box className={classes.root}>
    <Box className={classes.headerWrapper}>
      <Box className={classes.subWrapper}>
        <Box>
          <Text
            ffamily="Poppins"
            fWeight={500}
            variant="body1"
            className={classes.commonTStyle}
          >
            {title}
          </Text>
        </Box>
        <Box>
          <Text
            ffamily="Poppins"
            fWeight={700}
            variant="h4"
            color="#34A76C"
            className={classes.commonTStyle}
          >
            {subTitle}
          </Text>
        </Box>
      </Box>
      <Box className={classes.navStyle}>
        <IconButton className={classes.iconButton} onClick={previousDiv}>
          <Box className={classes.previous} />
        </IconButton>
        <IconButton className={classes.iconButton} onClick={nextDiv}>
          <Box className={classes.next} />
        </IconButton>
      </Box>
    </Box>
    <Box className={classes.scrollPad}>{children}</Box>
  </Box>
);

Carousel.propTypes = {
  title: PropTypes.string,
  subTitle: PropTypes.string,
  previousDiv: PropTypes.func,
  nextDiv: PropTypes.func,
};

export default withStyles(styles)(Carousel);
