import React from 'react'
import { Box, FormControl, InputLabel, Alert, Collapse } from '@material-ui/core'
import { withStyles } from '@material-ui/core/styles';
import PropTypes from 'prop-types';
import Text from '../text';
import Select from '../select'
import DatePicker from '../datePicker'
import TimePicker from '../timePicker'
import TextField from '@material-ui/core/TextField';
import Button from '../button'

const styles = {
    root : {
        width :350,
        backgroundColor : "rgba(238,238,238,0.95)",
        borderRadius : 10,
        padding : 20,
        paddingLeft : 20,
        paddingRight : 20
    },
    margin : {
        width : "100%"
    },
    marginTop : {
        marginTop : 15,
        width : "100%"
    },
    textField : {
        width : "100%"
    },

}

const Bookings = ({ classes, servicesPlaceholder, categoryData, onChangeCategory, onChangeServices, currentCategory, currentService, serviceData, handleDateChange, selectedDate, handleTimeChange, selectedTime, onSubmit }) => {

    const [showError, setShowError] = useState
    return <Box className={classes.root}>
        <Box>
            <Text variant="h6" fWeight={600} ffamily="Poppins">Select Our Services</Text>
        </Box>
        <Box>
            <Select
                placeholder="Select Category"
                options={categoryData}
                handleChange={onChangeCategory}
                value={currentCategory}
            />  
            <Select
                placeholder="Select Services"
                options={serviceData}
                handleChange={onChangeServices}
                value={currentService}
                multiple={true}
            />    
            <DatePicker 
                selectedDate={selectedDate}
                handleDateChange={handleDateChange}
            />  
            <TimePicker 
                selectedTime={selectedTime}
                handleTimeChange={handleTimeChange}
            />
            <Box className={classes.marginTop}>
                <TextField
                    id="outlined-multiline-static"
                    label="Description"
                    multiline
                    rows={2}
                    variant="outlined"
                    className={classes.textField}
                />
            </Box>
            <Box >
                <Button className={classes.marginTop} onClick={onSubmit}>Submit</Button>
            </Box>
        </Box>
        <Collapse in={showError}>
            <Alert severity="error">This is an error alert — check it out!</Alert>
        </Collapse>
    </Box>
}

Bookings.propTypes = {
    servicesPlaceholder : PropTypes.string,
    categoryData : PropTypes.array,
    onChangeCategory : PropTypes.func,
    onChangeServices : PropTypes.func,
    currentCategory : PropTypes.string,
    currentService : PropTypes.string,
    serviceData : PropTypes.array,
    selectedDate : PropTypes.string,
    handleDateChange : PropTypes.func,
    selectedTime : PropTypes.string,
    handleTimeChange : PropTypes.func,
    onSubmit : PropTypes.func,
    // eslint-disable-next-line react/forb,id-prop-types
    classes: PropTypes.object.isRequired,
}

export default withStyles(styles)(Bookings)
