import React, { useEffect, useState, useReducer, useContext } from 'react'
import { withStyles, makeStyles } from '@material-ui/core/styles'
import PropTypes from 'prop-types'
import { Box, Container, Grid, Paper, TextField, Collapse } from '@material-ui/core'
import Alert from '@material-ui/lab/Alert';
import Text from '../../components/text'
import Booking from '../../components/bookings' 
import "react-multi-carousel/lib/styles.css";
import Carousel from 'react-multi-carousel';
import ImageCard from '../../components/imageCard'
import CustomCarousel from '../../components/carousel/index2.js'
import IconCard from '../../components/iconCard'
import Button from '../../components/button'
import { subCatService, bookService } from '../../lib/services/home'
import io from 'socket.io-client'
import { getCookie } from '../../lib/utility/apiWrapper'
import Login from '../../components/loginModal'
import SignUp from '../../components/signUp'
import { store } from '../../lib/utility/store'
import { signUpStatus } from '../../lib/utility/actions'
import { reducer } from '../../lib/utility/reducer'
import { GlobalContext } from '../../../pages/_app'
import { LOGIN_MODAL_STATUS, SIGN_UP_MODAL_STATUS, ALERT_DIALOG, ERROR_TITLE, ERROR_DESCRIPTION, CATEGORY_LIST } from '../../lib/utility/type'
import AlertDialog from '../../components/alert'
import { categoiesList } from '../../lib/utility/actions'
import { useRouter } from 'next/router'


const styles = (theme) => ({
    root : {

    },
    form : {
        position : "absolute",
        top : 0,
        width : "100%",
        [theme.breakpoints.between('xs','sm')] : {
            position : "relative",
            marginTop : 20
        },
        [theme.breakpoints.up('sm')] : {
            position : 'absolute',
            marginTop : 20
        },
    },
    topContainer : {
        position : "relative",
        marginTop : 40,
        paddingBottom : 100,
        [theme.breakpoints.between('xs', 'sm')] : { 
            paddingBottom : 20
        }
    },
    pageTitle : {
        display : "flex",
        flexDirection : "row",
        paddingBottom : 15
    },
    titleText : {
        margin : 0,
        marginRight : 5,
        [theme.breakpoints.between('xs', 'sm')] : { 
            fontSize : "1.6rem"
        }
    },
    titleText2 : {
        margin : 0,
        [theme.breakpoints.between('xs', 'sm')] : { 
            fontSize : "1.6rem"
        }
    },
    imageBox : {
        width : 270,
        minWidth :  270,
        height : 300,
        backgroundImage : props => `url(/static/images/car_repair.jpg)`,
        backgroundSize : "cover",
        borderRadius : 10,
        // marginRight : 30
    },
    imageCarousel : {
        display : "flex",
        flexDirection : "row",
        // overflow : "hidden",
    },
    imageWrapper : {
        display : "flex",
        flexDirection : "row",  
        overflow : "hidden"
    },
    headingWrapper : {
        paddingTop : 20,
        paddingBottom : 15,
        [theme.breakpoints.between(700,1024)] : { 
            paddingTop : 78,
        },
        [theme.breakpoints.up(1024)] : { 
            paddingTop : 30,
        }
    },
    bookingWrapper : {
        [theme.breakpoints.between('xs', 'sm')] : {
            justifyContent : "center"
        },
        [theme.breakpoints.up('sm')] : {
            justifyContent : "flex-end"
        },
    },
    aboutUsImage : {
        height : 350,
        width : 320,
        backgroundImage : 'url(/static/images/car_repair.jpg)',
        backgroundSize : "cover",
        backgroundRepeat : "no-repeat",
        borderRadius : 10,
        [theme.breakpoints.down('sm')] : {
            width : "80%"
        },
        [theme.breakpoints.up('sm')] : {
            width : 320,
        },
        [theme.breakpoints.between('sm',"md")] : {
            width : "80%",
        },
        [theme.breakpoints.up("md")] : {
            width : "85%",
        },

    },
    upperDots : {
        position : "absolute",
        top : 0,
        left  : 0,
        width : 102,
        height : 120,
        backgroundImage : 'url(/static/images/dots.png)',
        backgroundSize : "cover",
        backgroundRepeat : "no-repeat",
        zIndex : -1,

    },
    lowerDots : {
        position : "absolute",
        bottom : 0,
        right  : 0,
        width : 102,
        height : 120,
        backgroundImage : 'url(/static/images/dots.png)',
        backgroundSize : "cover",
        backgroundRepeat : "no-repeat",
        zIndex : -1,
    },
    aboutUsWrapper : {
        position : "relative",
        height : 420,
        width : 420,
        display : "flex",
        justifyContent : "center",
        alignItems : "center",
        [theme.breakpoints.down("sm")] : {
            // width : "100%",
            marginTop : 20
        },

    },
    aboutUsMain : {
        display : "flex",
        justifyContent : "flex-end"
    },
    aboutUsContent : {
        display : "flex",
        justifyContent : " center"
    }, 
    firstRow : {
        [theme.breakpoints.only("sm")] : {
            paddingBottom : 70
        }
    },
    commonStyle : {
        marginBottom : 30
    },
    subscribeWrapper : {
        backgroundColor: "#FFFFFF",
        boxShadow: "0px 3px 6px rgba(0, 0, 0, 0.16)",
        borderRadius: 10,
        display : "flex",
        flexDirection : "row",
        justifyContent : "space-between",
        paddingTop : 30,
        paddingLeft : 50,
        paddingRight : 50,
        paddingBottom : 30,
        [theme.breakpoints.down('md')] : {
            flexDirection : "column",
        }
    },
    subscribeInputWrapper : {
        display : "flex",
        justifyContent : "space-between",
        [theme.breakpoints.down('md')] : {
            flexDirection : "column"
        }
    },
    inputWidth : {
        width  : "65%",
        [theme.breakpoints.down('md')] : {
            width : "100%",
            marginBottom : 10
        }
    },
    subscribeButton : {
        width : "100%",
        height : "100%"
    },
    mailBox : {
        backgroundImage : 'url(/static/images/mailBox.svg)',
        backgroundSize : "contain",
        backgroundRepeat : "no-repeat",
        width : 200,
        [theme.breakpoints.down("md")] : {
            display : "none"
        }
    },
    leftBox : {
        width : "65%",
        [theme.breakpoints.down("md")] : {
            width : "100% !important"
        }
    },
    subscribeButtonWrappper : {
        width : "35%",
        [theme.breakpoints.down("md")] : {
            width : "100% !important"
        }
    },
    inputField : {
        width : "90%",
        [theme.breakpoints.down("md")] : {
            width : "100% !important",
        }
    }
})

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    paper: {
      padding: theme.spacing(1),
      textAlign: 'center',
      color: theme.palette.text.secondary,
    },
  }));


let socket 

const Home = ({ classes }) => {
    const useClasses = useStyles();
    const [ categorySelect, setCategorySelect ] = useState([])
    const [ currentCategory, setCurrentCategory ] = useState(null)
    const [ currentService, setCurrentService ] = useState([])
    const [ groupId , setGroupId ] = useState(null)
    const [ serviceList, setServiceList ] = useState({})
    const [ serviceData, setServiceData ] = useState([])
    const [ selectedDate,setSelectedDate ] = useState(null)
    const [ selectedTime, setSelectedTime ] = useState(null)
    const [ catList, setCatList ] = useState(null)
    const [ errorTitle, setErrorTitle ] = useState(null)
    const [ errorDescription, setErrorDescription ] = useState(null)
    const [ openLogin, setOpenLogin ] = useState(false)
    const [ openSignUp, setOpenSignUp ] = useState(false)

    const [ images, setImages ] = useState([{url : "/static/images/car_repair.jpg"},{url : "/static/images/tailor.jpg"},{url : "/static/images/car_repair.jpg"},{url : "/static/images/tailor.jpg"},{url : "/static/images/car_repair.jpg"},{url : "/static/images/car_repair.jpg"}])

    const [ reducerState, dispatch ] = useReducer(reducer,store)
    const globalContext = useContext(GlobalContext)
    const [ showError, setShowError ] = useState(false)
    const router = useRouter()


    const responsive = {
        desktop: {
          breakpoint: { max: 3000, min: 900 },
          items: 3,
          slidesToSlide: 1
        },
        tablet: {
          breakpoint: { max: 900, min: 500 },
          items: 3,
          slidesToSlide: 1
        },
        mobile: {
          breakpoint: { max: 500, min: 0 },
          items: 1,
          slidesToSlide: 1
        }
    }

    useEffect( async () => {
        await subCatService().then((cdata) => {
            let tempArr = []
            let tempArr2 = []
            let tempArr3 = []
            let tempObj = {}
            let tempGid = {}
            let tempObj2 = {}
            cdata.data.map((eachData) => {
                if(eachData.sub_category && eachData.icon_url ){
                    tempArr2 = []
                    tempArr3 = []
                    eachData.service.map((serviceData) => {
                        tempArr2.push({ id : serviceData._id, label : serviceData.service })
                        tempArr3.push(serviceData.service)
                        tempObj2 = { [eachData._id] : { mainService : eachData.sub_category, service_list : tempArr3 }, ...tempObj2 }
                    })
                    console.log("Here is tempObj2",tempObj2)
                    tempObj = { [eachData._id] : tempArr2, ...tempObj }
                    tempGid = { [eachData._id] : eachData.group_id, ...tempGid}
                    tempArr.push({ id : eachData._id, label : eachData.sub_category, icon_url : eachData.icon_url, bannerUrl : eachData.banner_url })
                }
            })
            setCategorySelect(tempArr)
            setServiceList(tempObj)
            setGroupId(tempGid)
            setCatList(tempObj2)
            globalContext.allDispatch({ 
                type : CATEGORY_LIST,
                value : {
                    categories : tempArr, services : tempObj, cGroups : tempGid 
                }
            })
        })
        if(!socket){
            socket = io.connect('http://localhost:8080');
        }
    },[])

    const onChangeCategory = (e) => {
        setCurrentCategory(e.target.value)
        setServiceData(serviceList[e.target.value])
    }

    const onChangeServices = (e) => {
        setCurrentService(e.target.value)
    }

    const handleDateChange = (date) => {
        setSelectedDate(date)
    }

    const handleTimeChange = (time) => {
        setSelectedTime(time)
    }

    const onSubmit = () => {
        if(currentCategory && currentService && selectedDate && selectedTime ){
            if(getCookie('token')){
                let finalData = {
                    sub_category_id : currentCategory,
                    group_id : groupId[currentCategory],
                }
                // console.log("Tenali raman 1",getCookie('purchaseObj'))

                // console.log("Hello ninja",catList[currentCategory])
                // document.cookie = `purchaseObj=${JSON.stringify(catList[currentCategory])}`
                // console.log("Here is category list",catList[currentCategory])
                router.push(() => ({
                    pathname: '/order',
                    query: {
                        categoryName : catList[currentCategory]
                    },
                }))
            }else{
                globalContext.allDispatch({
                    type : LOGIN_MODAL_STATUS,
                    value : true
                })
            }
        }else{
            globalContext.allDispatch({
                type : ERROR_DESCRIPTION,
                value : "Please enter all mandatory fields."
            })
            globalContext.allDispatch({
                type : ERROR_TITLE,
                value : "Missing Fields"
            })
            globalContext.allDispatch({
                type : ALERT_DIALOG,
                value : true
            })
        }
    }

    const closeLogin  = () => {
        globalContext.allDispatch({
            type : LOGIN_MODAL_STATUS,
            value : false
        })
    }

    const closeSignUp = () => {
        globalContext.allDispatch({
            type : SIGN_UP_MODAL_STATUS,
            value : false
        })
    }

    return (<Box>
            <Box className={classes.commonStyle}>
                <Container  className={classes.firstRow}>
                    <Grid container direction="column" className={classes.topContainer}>          
                        <Grid item xs={12} sm={6} md={6} lg={6} className={classes.headingWrapper}>
                            <Box className={classes.pageTitle}>
                                <Text variant="h4" ffamily={"Poppins"} fWeight={700} color="#34A76C" className={classes.titleText}>Anytime</Text>
                                <Text variant="h4" ffamily={"Poppins"} fWeight={700} className={classes.titleText2}> Anywhere</Text>
                            </Box>
                            <Box className={classes.subTitle}>
                                <Text variant="body1" ffamily={"Poppins"} fWeigth={600} color="">We at Our company gives smooth transaction between user and vendor</Text>
                            </Box>
                        </Grid>
                        <Grid item className={classes.imageCarousel} xs={12} sm={12} md={12} lg={12}>
                            <Box className={classes.imageWrapper}>
                                <Carousel
                                    swipeable={false}
                                    draggable={false}
                                    showDots={false}
                                    responsive={responsive}
                                    ssr={true} // means to render carousel on server-side.
                                    infinite={true}
                                    autoPlay={true}
                                    autoPlaySpeed={2000}
                                    // keyBoardControl={true}
                                    customTransition="all .5"
                                    transitionDuration={500}
                                    containerClass="carousel-container"
                                    removeArrowOnDeviceType={["tablet", "mobile","desktop"]}
                                    deviceType={["tablet"]}
                                    // dotListClass="custom-dot-list-style"
                                    // itemClass="carousel-item-padding-30-px"
                                >
                                    {
                                        images.map((data,index) => {
                                            return <ImageCard key={index.toString()} image={data.url} />
                                        })
                                    }
                                </Carousel>
                            </Box>
                        </Grid>
                        <Grid item className={classes.form}>
                            <Grid
                                container
                                direction="row"
                                justify="flex-end"
                                alignItems="center"
                                className={classes.bookingWrapper}
                            >    
                                <Booking 
                                    categoryData={categorySelect}
                                    onChangeCategory={onChangeCategory}
                                    currentCategory={currentCategory}
                                    currentService={currentService}
                                    onChangeServices={onChangeServices}
                                    serviceData={serviceData}
                                    handleDateChange={handleDateChange}
                                    selectedDate={selectedDate}
                                    selectedTime={selectedTime}
                                    handleTimeChange={handleTimeChange}
                                    onSubmit={onSubmit}
                                />
                            </Grid>
                        </Grid>
                    </Grid>
                </Container>    
            </Box>
            <Box className={classes.commonStyle}>
                <Container>
                    <Grid container direction="row" >
                        <Grid item xs={12} sm={6} md={7} lg={6} xl={6} className={classes.aboutUsContent}>
                            <Grid container direction="column" className={classes.aboutUsContent}> 
                                <Grid item> 
                                    <Box className={classes.pageTitle}>
                                        <Text variant="h4" ffamily={"Poppins"} fWeight={700} color="#34A76C" className={classes.titleText}>About Us</Text>
                                    </Box>
                                </Grid>
                                <Grid item> 
                                    <Text variant={"body2"}>Folly words widow one downs few age every seven. If miss part by fact he park just shew. Discovered had get considered projection who favourable. Necessary up knowled any chatty can elinor direct for former. Up as meant widow equal an share leasge it tolerably. Unwilling departure education is be dashwoods or an. Use off agreeable law unwilling sir deficient curiosity instantly. Easy mind life fact with see has bore ten. Parisht. </Text>
                                </Grid>
                            </Grid>
                        </Grid>
                        <Grid item xs={12} sm={6} md={5} lg={6} xl={6}>
                            <Grid conatiner justify="flex-end" direction="row" className={classes.aboutUsMain}>
                                <Box className={classes.aboutUsWrapper}>
                                    <Box className={classes.upperDots}></Box>
                                    <Box className={classes.aboutUsImage} />
                                    <Box className={classes.lowerDots}></Box>                       
                                </Box>
                            </Grid>
                        </Grid>
                    </Grid>
                </Container>    
            </Box>
            <Box className={classes.commonStyle}>
                <Container>
                    <Grid container direction="row" >
                        <CustomCarousel
                            title="Best services"
                            subTitle={"Explore Our Services"}
                            scrollOffset={220}
                        >
                            {
                                categorySelect && Array.isArray(categorySelect) && categorySelect.map((data,index) => {
                                    console.log("Hello kital",data.id)
                                    return <IconCard indexNo={data.id} serviceName={data.label} imageUrl={data.icon_url} />
                                })
                            }
                        </CustomCarousel>    
                    </Grid>
                </Container>
            </Box>
            <Box className={classes.commonStyle}>
            <Container>
                <Box className={classes.subscribeWrapper}>
                    <Box style={{ width : "60%"}} className={classes.leftBox}>
                        <Box style={{ marginBottom : 20}}> 
                            <Text variant="h4" ffamily={"Poppins"} fWeight={700} color="#34A76C" className={classes.titleText}>Subscribe To Get Latest News</Text>
                        </Box>
                        <Box style={{ marginBottom : 20}}>
                            <Text variant="h6" ffamily={"Poppins"} fWeight={400}  className={classes.titleText}>Enter your email address to get our latest news. </Text>
                        </Box>
                        <Box className={classes.subscribeInputWrapper}>
                            <Box className={classes.inputWidth} >
                                <TextField id="outlined-basic" label="Outlined" variant="outlined" className={classes.inputField} size="small"/>
                            </Box>
                            <Box className={classes.subscribeButtonWrappper}>
                                <Button className={classes.subscribeButton}>Subscribe</Button>
                            </Box>
                        </Box>
                    </Box>
                    <Box className={classes.mailBox}></Box>
                </Box>
            </Container>
            </Box>
            <Login open={globalContext.state.loginStatus} handleClose={closeLogin} />
            <SignUp open={globalContext.state.signUpStatus} handleClose={closeSignUp} />
            <AlertDialog open={globalContext.state.alertStatus} title={globalContext.state.errorTitle} description={globalContext.state.errorDescription} />
        </Box>)
}

Home.protoTypes = {
    // eslint-disable-next-line react/forbid-prop-types
    classes: PropTypes.object.isRequired,
}

export default withStyles(styles)(Home)
