import React, { useContext } from 'react'
import { Box, Container } from '@material-ui/core'
import { withStyles } from '@material-ui/core/styles'
import PropTypes from 'prop-types'
import { Collapse, ListItem, ListItemText, List, ListSubheader } from '@material-ui/core';
import Buttons from '../../components/button';
import { useRouter } from 'next/router'
import { GlobalContext } from '../../../pages/_app'
import { ExpandLess, ExpandMore } from '@material-ui/icons';
import Text from '../../components/text'

const styles = (theme) => ({
    root : {},
    banner : {
        backgroundImage : props => `url(${props.bannerImage})`,
        backgroundRepeat : "no-repeat",
        backgroundSize : "cover",
        width : "100%",
        height : 300,
        borderRadius : 10,
        boxShadow: "0px 16px 24px rgba(0, 0, 0, 0.06), 0px 2px 6px rgba(0, 0, 0, 0.04), 0px 0px 1px rgba(0, 0, 0, 0.04)",
        marginTop : 20,
        marginBottom : 20,
        position : "relative"
    },
    subBanner : {
        position : "absolute",
        width : "100%",
        // height : "100%",
        display : "flex",
        flexDirection : "row",
        justifyContent : "flex-end",
        padding : 30
    },
    button : {
        width : 100,
    },
    nested: {
        paddingLeft: theme.spacing(4),
        fontFamily : "Poppins"
    },
})

const ServicePage = ({ classes, bannerImage }) => {
    const router = useRouter()
    const [open, setOpen] = React.useState(true);

    const globalContext = useContext(GlobalContext)
    console.log("tavera",router.query.indexNo,globalContext.state.categoryList.categories[router.query.indexNo].bannerUrl);

    const onClickCollapse = () => {
        setOpen(!open);
    }

    return(
        <Container>
            <Box 
                className={classes.banner} 
                style={{ backgroundImage : `url(${globalContext.state.categoryList.categories[router.query.indexNo].bannerUrl})`}}
            >
                <Box className={classes.subBanner}>
                    <Buttons className={classes.button}>
                        Book Now
                    </Buttons>
                </Box>
            </Box>
            <Box>
                <Text ffamily="Poppins" fWeight={500} variant={"h6"}>
                    Service Name
                </Text>
                <Text ffamily="Poppins" variant={"body2"} component="p">
                    Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.
                </Text>
            </Box>
            <List
                component="nav"
                aria-labelledby="nested-list-subheader"
            >
                <ListItem button onClick={onClickCollapse}>
                    <ListItemText className={classes.list} primary="It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. " />
                    {open ? <ExpandLess /> : <ExpandMore />}
                </ListItem> 
                <Collapse in={open} timeout="auto" unmountOnExit>
                    {/* <List component="div" variant="caption" disablePadding>
                        <ListItem button className={classes.nested}> */}
                            <Text className={classes.nested} variant="body2">
                                Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
                            </Text>
                        {/* </ListItem>
                    </List> */}
                </Collapse>
            </List>
        </Container>
    )
}

ServicePage.propTypes ={
    bannerImage : PropTypes.string,
    // eslint-disable-next-line react/forb,id-prop-types
    classes: PropTypes.object.isRequired,
}

export default withStyles(styles)(ServicePage)
