import React, { useState } from 'react'
import { withStyles, makeStyles } from '@material-ui/core/styles'
import PropTypes from 'prop-types'
import { Box, Container, Grid, Paper } from '@material-ui/core'
import Text from '../../components/text'
import Booking from '../../components/bookings' 
import "react-multi-carousel/lib/styles.css";
import Carousel from 'react-multi-carousel';
import ImageCard from '../../components/imageCard'

const styles = (theme) => ({
    root : {

    },
    form : {
        position : "absolute",
        top : 0,
        width : "100%",
        [theme.breakpoints.down('sm')] : {
            position : "relative",
            marginTop : 20
        },
        [theme.breakpoints.up('md')] : {
            position : "relative",
            marginTop : 20
        }
    },
    topContainer : {
        position : "relative",
        marginTop : 40
    },
    pageTitle : {
        display : "flex",
        flexDirection : "row"
    },
    titleText : {
        margin : 0,
        marginRight : 5,
        [theme.breakpoints.between('xs', 'sm')] : { 
            fontSize : "1.6rem"
        }
    },
    titleText2 : {
        margin : 0,
        [theme.breakpoints.between('xs', 'sm')] : { 
            fontSize : "1.6rem"
        }
    },
    imageBox : {
        width : 270,
        minWidth :  270,
        height : 300,
        backgroundImage : props => `url(/static/images/car_repair.jpg)`,
        backgroundSize : "cover",
        borderRadius : 10,
        // marginRight : 30
    },
    imageCarousel : {
        display : "flex",
        flexDirection : "row",
        // overflow : "hidden",
    },
    imageWrapper : {
        display : "flex",
        flexDirection : "row",  
        overflow : "hidden"
    },
    headingWrapper : {
        paddingTop : 20,
        paddingBottom : 15
    },
    bookingWrapper : {
        [theme.breakpoints.between('xs', 'sm')] : {
            justifyContent : "center"
        }
    }
})

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    paper: {
      padding: theme.spacing(1),
      textAlign: 'center',
      color: theme.palette.text.secondary,
    },
  }));

const Home = ({ classes }) => {
    const useClasses = useStyles();
    const [images, setImages] = useState([{url : "/static/images/car_repair.jpg"},{url : "/static/images/tailor.jpg"},{url : "/static/images/car_repair.jpg"},{url : "/static/images/tailor.jpg"},{url : "/static/images/car_repair.jpg"},{url : "/static/images/car_repair.jpg"}])
    const responsive = {
        desktop: {
          breakpoint: { max: 3000, min: 900 },
          items: 3,
          slidesToSlide: 3
        },
        tablet: {
          breakpoint: { max: 900, min: 500 },
          items: 2,
          slidesToSlide: 2
        },
        mobile: {
          breakpoint: { max: 500, min: 0 },
          items: 1,
          slidesToSlide: 1
        }
      };
    return <Box>
        <Container>
            <Grid container direction="column" className={classes.topContainer}>
                <Grid item xs={12} sm={6} md={6} lg={6} className={classes.headingWrapper}>
                    <Box className={classes.pageTitle}>
                        <Text variant="h4" ffamily={"Poppins"} fWeight={700} color="#34A76C" className={classes.titleText}>Anytime</Text>
                        <Text variant="h4" ffamily={"Poppins"} fWeight={700} className={classes.titleText2}> Anywhere</Text>
                    </Box>
                    <Box className={classes.subTitle}>
                        <Text variant="body1" ffamily={"Poppins"} fWeigth={600} color="">We at Our company gives smooth transaction between user and vendor</Text>
                    </Box>
                </Grid>
                <Grid item className={classes.imageCarousel} xs={12} sm={12} md={12} lg={12}>
                    {/* <Box className={classes.imageWrapper}> */}
                    <Carousel
                        swipeable={false}
                        draggable={false}
                        showDots={false}
                        responsive={responsive}
                        ssr={true} // means to render carousel on server-side.
                        infinite={true}
                        autoPlay={true}
                        autoPlaySpeed={2000}
                        // keyBoardControl={true}
                        customTransition="all .5"
                        transitionDuration={500}
                        containerClass="carousel-container"
                        removeArrowOnDeviceType={["tablet", "mobile","desktop"]}
                        deviceType={["desktop"]}
                        // dotListClass="custom-dot-list-style"
                        // itemClass="carousel-item-padding-10-px"
                    >
                        {
                            images.map((data) => {
                                return <ImageCard image={data.url} />
                            })
                        }
                    </Carousel>
                    {/* </Box> */}
                </Grid>
                <Grid item className={classes.form}>
                    <Grid
                        container
                        direction="row"
                        justify="flex-end"
                        alignItems="center"
                        className={classes.bookingWrapper}
                    >
                        <Booking />
                    </Grid>
                </Grid>
            </Grid>
        </Container>
    </Box>
}

Home.protoTypes = {
    // eslint-disable-next-line react/forbid-prop-types
    classes: PropTypes.object.isRequired,
}

export default withStyles(styles)(Home)
