import React from 'react'
import { withStyles } from '@material-ui/core/styles'
import { Box } from '@material-ui/core'
import PropTypes from 'prop-types'
import Text from '../text'

const styles = (theme) => ({
    imageBox : {
        width : 150,
        height : 140,
        boxShadow: "0px 16px 24px rgba(0, 0, 0, 0.06), 0px 2px 6px rgba(0, 0, 0, 0.04), 0px 0px 1px rgba(0, 0, 0, 0.04)",
        borderRadius : 10,
        padding : 20,
        // backgroundImage : props => `url(${props.imageUrl})`,
        // backgroundSize : "contain",
        // backgroundRepeat : "no-repeat",
        marginBottom : 15,

    },
    root : {
        display : "flex",
        flexDirection : "column",
        marginRight : 25,
    },
    textAlign : {
        textAlign : "center"
    },
    image : {
        width : "90%",
        height : "90%"
    }
})

const IconCard = ({ classes, serviceName, imageUrl }) => {

    return <Box className={classes.root}>
            <Box className={classes.imageBox}>
                <img src={imageUrl} className={classes.image}/>
            </Box>
            <Text ffamily={"Poppins"} variant="body2" className={classes.textAlign}> { serviceName } </Text>
        </Box>
}


IconCard.propTypes = {
    serviceName : PropTypes.string,
    imageUrl : PropTypes.string,
    // eslint-disable-next-line react/forb,id-prop-types
    classes: PropTypes.object.isRequired,
}

export default withStyles(styles)(IconCard)
