import { Button } from '@material-ui/core';
import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import { bool, func, oneOf } from 'prop-types';

const buttonColor = {
  green: '#34A76C',
  white: '#ffffff',
  disabled: '#98979a',
};
const UnStyleButton = withStyles({
  root: (props) => ({
    fontFamily: 'Poppins',
    borderRadius: '5px',
    fontSize: '18px',
    backgroundColor:
      props.backgroundcolor === 'green' ? buttonColor.green : buttonColor.white,
    color:
      props.backgroundcolor === 'green' ? buttonColor.white : buttonColor.green,
    textTransform: 'unset',
    fontWeight: 'normal',
    '&:hover': {
      backgroundColor:
        props.backgroundcolor === 'green'
          ? buttonColor.green
          : buttonColor.white,
    },
    '&:disabled': {
      color: buttonColor.white,
      backgroundColor: buttonColor.disabled,
    },
  }),
})(Button);

const Buttons = ({
  children,
  className,
  disabled,
  onClick,
  backgroundcolor,
  size,
}) => (
  <UnStyleButton
    variant="contained"
    className={className}
    disabled={disabled}
    onClick={onClick}
    backgroundcolor={backgroundcolor}
    variant={variant}
    size={size}
  >
    {children}
  </UnStyleButton>
);
Buttons.defaultProps = {
  backgroundcolor: 'green',
  disabled: false,
  size: 'medium',
};
Buttons.propTypes = {
  disabled: bool,
  onClick: func,
  backgroundcolor: oneOf(['green', 'white']),
  size: oneOf(['medium', 'small', 'large']),
};

export default Buttons;
