import { GlobalContext } from '../../../pages/_app'
import { LOGIN_MODAL_STATUS } from '../../lib/utility/type'

export const login = async (data) => {
    const globalContext = useContext(GlobalContext)

    let loginData = await request({ url : '/userlogin', method : "post", data  })
    if(loginData.result === "success"){
        document.cookie = `token=${loginData.token}`
        let stringifiedData = JSON.stringify(loginData.data)
        localStorage.setItem('userInfo',stringifiedData)
        globalContext.allDispatch({
            type : AUTH_STATUS,
            value : true
        })
