        <Box className={classes.root} onClick={onClickDiv}>
            <Box className={classes.addressCard}>
                <Text>{address1 || "Add address"}</Text>
                <Text>{address2}</Text>
            </Box>
            {
                !address1 ? 
                    <Buttons className={classes.selectedButton} onClick={onClickDiv} >Add Address</Buttons>
                    :
                    null
            }
            {
                address1 ? 

                    selected ? 
                        <Buttons className={classes.selectedButton}  onClick={onSelected} >Selected</Buttons>
                        :
                        <Buttons className={classes.button} onClick={onSelected} >Select</Buttons>
                    :
                    null
            }
        </Box>
    )
}

AddressCard.propTypes = {
    address1 : PropTypes.string,
    address2 : PropTypes.string,
    selected : PropTypes.bool,
    image : PropTypes.string,
    adderListId : PropTypes.number,
    onClickDiv : PropTypes.func
}

export default withStyles(styles)(AddressCard)
