import React, { useState } from 'react'
import { withStyles, makeStyles } from '@material-ui/core/styles'
import PropTypes from 'prop-types'
import { Box, Container, Grid, Paper } from '@material-ui/core'
import Text from '../../components/text'
import Booking from '../../components/bookings' 
import "react-multi-carousel/lib/styles.css";
import Carousel from 'react-multi-carousel';
import ImageCard from '../../components/imageCard'

const styles = (theme) => ({
    root : {

    },
    form : {
        position : "absolute",
        top : 0,
        width : "100%",
        [theme.breakpoints.between('xs','sm')] : {
            position : "relative",
            marginTop : 20
        },
        [theme.breakpoints.up('sm')] : {
            position : 'absolute',
            marginTop : 20
        },

    },
    topContainer : {
        position : "relative",
        marginTop : 40
    },
    pageTitle : {
        display : "flex",
        flexDirection : "row"
    },
    titleText : {
        margin : 0,
        marginRight : 5,
        [theme.breakpoints.between('xs', 'sm')] : { 
            fontSize : "1.6rem"
        }
    },
    titleText2 : {
        margin : 0,
        [theme.breakpoints.between('xs', 'sm')] : { 
            fontSize : "1.6rem"
        }
    },
    imageBox : {
        width : 270,
        minWidth :  270,
        height : 300,
        backgroundImage : props => `url(/static/images/car_repair.jpg)`,
        backgroundSize : "cover",
        borderRadius : 10,
        // marginRight : 30
    },
    imageCarousel : {
        display : "flex",
        flexDirection : "row",
        // overflow : "hidden",
    },
    imageWrapper : {
        display : "flex",
        flexDirection : "row",  
        overflow : "hidden"
    },
    headingWrapper : {
        paddingTop : 20,
        paddingBottom : 15,
        [theme.breakpoints.between(700,1024)] : { 
            paddingTop : 78,
        },
        [theme.breakpoints.up(1024)] : { 
            paddingTop : 30,
        }
    },
    bookingWrapper : {
        [theme.breakpoints.between('xs', 'sm')] : {
            justifyContent : "center"
        },
        [theme.breakpoints.up('sm')] : {
            justifyContent : "flex-end"
        },
    },
    aboutUsImage : {
        height : 350,
        width : 320,
        backgroundImage : 'url(/static/images/car_repair.jpg)',
        backgroundSize : "cover",
        backgroundRepeat : "no-repeat",
        borderRadius : 10,
    },
    upperDots : {
        position : "absolute",
        top : 0,
        left  : 0,
        width : 102,
        height : 120,
        backgroundImage : 'url(/static/images/dots.png)',
        backgroundSize : "cover",
        backgroundRepeat : "no-repeat",
    },
    lowerDots : {
        position : "absolute",
        bottom : 0,
        right  : 0,
        width : 102,
        height : 120,
        backgroundImage : 'url(/static/images/dots.png)',
        backgroundSize : "cover",
        backgroundRepeat : "no-repeat",
        zIndex : -1,
    },
    aboutUsWrapper : {
        position : "relative",
        height : 420,
        width : 420,
        display : "flex",
        justifyContent : "center",
        alignItems : "center"
    },
    aboutUsMain : {
        display : "flex",
        justifyContent : "flex-end"
    }
})

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    paper: {
      padding: theme.spacing(1),
      textAlign: 'center',
      color: theme.palette.text.secondary,
    },
  }));
//   
const Home = ({ classes }) => {
    const useClasses = useStyles();
    const [images, setImages] = useState([{url : "/static/images/car_repair.jpg"},{url : "/static/images/tailor.jpg"},{url : "/static/images/car_repair.jpg"},{url : "/static/images/tailor.jpg"},{url : "/static/images/car_repair.jpg"},{url : "/static/images/car_repair.jpg"}])
    const responsive = {
        desktop: {
          breakpoint: { max: 3000, min: 900 },
          items: 3,
          slidesToSlide: 1
        },
        tablet: {
          breakpoint: { max: 900, min: 500 },
          items: 3,
          slidesToSlide: 1
        },
        mobile: {
          breakpoint: { max: 500, min: 0 },
          items: 1,
          slidesToSlide: 1
        }
      };
    return <Box>
        <Container>
            <Grid container direction="column" className={classes.topContainer}>
                <Grid item xs={12} sm={6} md={6} lg={6} className={classes.headingWrapper}>
                    <Box className={classes.pageTitle}>
                        <Text variant="h4" ffamily={"Poppins"} fWeight={700} color="#34A76C" className={classes.titleText}>Anytime</Text>
                        <Text variant="h4" ffamily={"Poppins"} fWeight={700} className={classes.titleText2}> Anywhere</Text>
                    </Box>
                    <Box className={classes.subTitle}>
                        <Text variant="body1" ffamily={"Poppins"} fWeigth={600} color="">We at Our company gives smooth transaction between user and vendor</Text>
                    </Box>
                </Grid>
                <Grid item className={classes.imageCarousel} xs={12} sm={12} md={12} lg={12}>
                    {/* <Box className={classes.imageWrapper}> */}
                    <Carousel
                        swipeable={false}
                        draggable={false}
                        showDots={false}
                        responsive={responsive}
                        ssr={true} // means to render carousel on server-side.
                        infinite={true}
                        autoPlay={true}
                        autoPlaySpeed={2000}
                        // keyBoardControl={true}
                        customTransition="all .5"
                        transitionDuration={500}
                        containerClass="carousel-container"
                        removeArrowOnDeviceType={["tablet", "mobile","desktop"]}
                        deviceType={["tablet"]}
                        // dotListClass="custom-dot-list-style"
                        // itemClass="carousel-item-padding-30-px"
                    >
                        {
                            images.map((data,index) => {
                                return <ImageCard key={index.toString()} image={data.url} />
                            })
                        }
                    </Carousel>
                    {/* </Box> */}
                </Grid>
                <Grid item className={classes.form}>
                    <Grid
                        container
                        direction="row"
                        justify="flex-end"
                        alignItems="center"
                        className={classes.bookingWrapper}
                    >
                        <Booking />
                    </Grid>
                </Grid>
            </Grid>
        </Container>
        <Container>
            <Grid container direction="row" >
                <Grid item xs={12} sm={12} md={6} lg={6} xl={6}>
                    <Grid container direction="column">
                        <Grid item> 
                            <Box className={classes.pageTitle}>
                                <Text variant="h4" ffamily={"Poppins"} fWeight={700} color="#34A76C" className={classes.titleText}>About Us</Text>
                            </Box>
                        </Grid>
                        <Grid item> 
                            <Text variant={"body2"}>Folly words widow one downs few age every seven. If miss part by fact he park just shew. Discovered had get considered projection who favourable. Necessary up knowled any chatty can elinor direct for former. Up as meant widow equal an share leasge it tolerably. Unwilling departure education is be dashwoods or an. Use off agreeable law unwilling sir deficient curiosity instantly. Easy mind life fact with see has bore ten. Parisht. </Text>
                        </Grid>
                    </Grid>
                </Grid>
                <Grid item xs={12} sm={12} md={6} lg={6} xl={6}>
                    <Grid conatiner justify="flex-end" direction="row" className={classes.aboutUsMain}>
                        <Box className={classes.aboutUsWrapper}>
                            <Box className={classes.upperDots}></Box>
                            <Box className={classes.aboutUsImage} />
                            <Box className={classes.lowerDots}></Box>                       
                        </Box>
                    </Grid>
                </Grid>
            </Grid>
        </Container>

    </Box>
}

Home.protoTypes = {
    // eslint-disable-next-line react/forbid-prop-types
    classes: PropTypes.object.isRequired,
}

export default withStyles(styles)(Home)
