import React from 'react'
import { withStyles } from '@material-ui/core/styles'
import { Box } from '@material-ui/core'
import PropTypes from 'prop-types'
import Text from '../text'
import Link from 'next/link'

const styles = (theme) => ({
    imageBox : {
        width : 150,
        height : 140,
        boxShadow: "0px 16px 24px rgba(0, 0, 0, 0.06), 0px 2px 6px rgba(0, 0, 0, 0.04), 0px 0px 1px rgba(0, 0, 0, 0.04)",
        borderRadius : 10,
        padding : 20,
        // backgroundImage : props => `url(${props.imageUrl})`,
        // backgroundSize : "contain",
        // backgroundRepeat : "no-repeat",
        marginBottom : 15,
        display : "flex",
        justifyContent : "center",
        alignItems : "center"
    },
    root : {
        display : "flex",
        flexDirection : "column",
        marginRight : 25,
        cursor : "pointer",
        [theme.breakpoints.down(768)]: {
            marginRight : 0
        },
        [theme.breakpoints.up(768)]: {
            marginRight : 25
        },
    },
    textAlign : {
        textAlign : "center"
    },
    image : {
        width : "70%",
        height : "70%"
    }
})

const IconCard = ({ classes, serviceName, imageUrl, index }) => {

    const onClickCard = () => {
        console.log("I am clicked brother")
    }

    return <Link
                href={{
                    pathname: '/services',
                    query: { id: 'test', indexNo :  key },
                }}
            >
                <Box className={classes.root} onClick={onClickCard}>
                    <Box className={classes.imageBox}>
                        <img src={imageUrl} className={classes.image}/>
                    </Box>
                    <Text ffamily={"Poppins"} variant="caption" fWeight={600} className={classes.textAlign}> { serviceName } </Text>
                </Box>
            </Link>
}


IconCard.propTypes = {
    serviceName : PropTypes.string,
    imageUrl : PropTypes.string,
    key : PropTypes.number,
    // eslint-disable-next-line react/forb,id-prop-types
    classes: PropTypes.object.isRequired,
}

export default withStyles(styles)(IconCard)
