import React, { useState } from 'react'
import { withStyles, makeStyles } from '@material-ui/core/styles'
import PropTypes from 'prop-types'
import { Box, Container, Grid, Paper, TextField } from '@material-ui/core'
import Text from '../../components/text'
import Booking from '../../components/bookings' 
import "react-multi-carousel/lib/styles.css";
import Carousel from 'react-multi-carousel';
import ImageCard from '../../components/imageCard'
import CustomCarousel from '../../components/carousel/index2.js'
import IconCard from '../../components/iconCard'
import Button from '../../components/button'

const styles = (theme) => ({
    root : {

    },
    form : {
        position : "absolute",
        top : 0,
        width : "100%",
        [theme.breakpoints.between('xs','sm')] : {
            position : "relative",
            marginTop : 20
        },
        [theme.breakpoints.up('sm')] : {
            position : 'absolute',
            marginTop : 20
        },

    },
    topContainer : {
        position : "relative",
        marginTop : 40,
        paddingBottom : 100,
        [theme.breakpoints.between('xs', 'sm')] : { 
            paddingBottom : 20
        }
    },
    pageTitle : {
        display : "flex",
        flexDirection : "row",
        paddingBottom : 15
    },
    titleText : {
        margin : 0,
        marginRight : 5,
        [theme.breakpoints.between('xs', 'sm')] : { 
            fontSize : "1.6rem"
        }
    },
    titleText2 : {
        margin : 0,
        [theme.breakpoints.between('xs', 'sm')] : { 
            fontSize : "1.6rem"
        }
    },
    imageBox : {
        width : 270,
        minWidth :  270,
        height : 300,
        backgroundImage : props => `url(/static/images/car_repair.jpg)`,
        backgroundSize : "cover",
        borderRadius : 10,
        // marginRight : 30
    },
    imageCarousel : {
        display : "flex",
        flexDirection : "row",
        // overflow : "hidden",
    },
    imageWrapper : {
        display : "flex",
        flexDirection : "row",  
        overflow : "hidden"
    },
    headingWrapper : {
        paddingTop : 20,
        paddingBottom : 15,
        [theme.breakpoints.between(700,1024)] : { 
            paddingTop : 78,
        },
        [theme.breakpoints.up(1024)] : { 
            paddingTop : 30,
        }
    },
    bookingWrapper : {
        [theme.breakpoints.between('xs', 'sm')] : {
            justifyContent : "center"
        },
        [theme.breakpoints.up('sm')] : {
            justifyContent : "flex-end"
        },
    },
    aboutUsImage : {
        height : 350,
        width : 320,
        backgroundImage : 'url(/static/images/car_repair.jpg)',
        backgroundSize : "cover",
        backgroundRepeat : "no-repeat",
        borderRadius : 10,
        [theme.breakpoints.down('sm')] : {
            width : "80%"
        },
        [theme.breakpoints.up('sm')] : {
            width : 320,
        },
        [theme.breakpoints.between('sm',"md")] : {
            width : "80%",
        },
        [theme.breakpoints.up("md")] : {
            width : "85%",
        },

    },
    upperDots : {
        position : "absolute",
        top : 0,
        left  : 0,
        width : 102,
        height : 120,
        backgroundImage : 'url(/static/images/dots.png)',
        backgroundSize : "cover",
        backgroundRepeat : "no-repeat",
        zIndex : -1,

    },
    lowerDots : {
        position : "absolute",
        bottom : 0,
        right  : 0,
        width : 102,
        height : 120,
        backgroundImage : 'url(/static/images/dots.png)',
        backgroundSize : "cover",
        backgroundRepeat : "no-repeat",
        zIndex : -1,
    },
    aboutUsWrapper : {
        position : "relative",
        height : 420,
        width : 420,
        display : "flex",
        justifyContent : "center",
        alignItems : "center",
        [theme.breakpoints.down("sm")] : {
            // width : "100%",
            marginTop : 20
        },

    },
    aboutUsMain : {
        display : "flex",
        justifyContent : "flex-end"
    },
    aboutUsContent : {
        display : "flex",
        justifyContent : " center"
