import React, { useEffect, useState } from 'react'
import { Box, Paper, DialogTitle, Dialog, DialogContent, TextField, IconButton } from '@material-ui/core'
import { withStyles } from '@material-ui/core/styles'
import PropTypes from 'prop-types'
import Text from '../text'
import Button from '../button'
import CloseIcon from '@material-ui/icons/Close'
import MyMap from '../map'
// import GooglePlacesAutocomplete from 'react-google-places-autocomplete';
// import Autocomplete from 'react-google-autocomplete';


const styles = (theme) => ({
    root : {
        // width : "100vw",
        // height : "100vh",
        // backgroundColor : "rgba(0,0,0,0.3)",
        // display : "flex",
        // justifyContent : "center",
        // alignItems : "center",
        // position : "absolute",
    },
    map : {
        width : "100%",
        minHeight : 600,
        // marginBottom : 30,
        position : "relative",
        [theme.breakpoints.down('sm')] : {
            height : 165
        },
        [theme.breakpoints.between('sm','lg')] : {
            height : 220
        },
        // zIndex : 100000
    },
    title : {
        display : "flex",
        flexDirection : "row",
        justifyContent : "space-between",
        // marginBottom : 20
    }
})



const Location = ({ classes, handleClose, open }) => {

    const [latLan, setLatLan ] = useState({})
    const [showLatLan, setShowLatLan] = useState({ lat : 12.9716, lng : 77.5946 })
    const [value, setValue] = useState(null);

    useEffect(() => {
        navigator.geolocation.getCurrentPosition((position) => {
            let temp = {
                lat : position.coords.latitude,
                lng : position.coords.longitude
            }
            setLatLan(temp)
          });
    },[])



    const setPlace = (v) => {
        setValue(v)
        console.log("Here is value",v)
    }
    return <Box className={classes.root}>
        <Dialog
            open={open}
            onClose={handleClose}
            aria-labelledby="alert-dialog-title"
            aria-describedby="alert-dialog-description"
            fullWidth={true}
            maxWidth={"lg"}
            style={{
                zIndex : 900,
                minHeight : 600,
            }}
        >
            <Box className={classes.title}>
                <Text variant="h6" ffamily="Poppins">Add new address.</Text>
                <IconButton aria-label="delete">
                    <CloseIcon />
                </IconButton>
            </Box>

            <Box className={classes.map}>
                <MyMap lat={showLatLan.lat} lng={showLatLan.lng} isMarkerShown={true} />
            </Box>

        </Dialog>
    </Box>
}

Location.propTypes = {
    open : PropTypes.bool,
    handleClose : PropTypes.func,
    // eslint-disable-next-line react/forb,id-prop-types
    classes: PropTypes.object.isRequired,
}

export default withStyles(styles)(Location)
