import React, { useContext, useState } from 'react';
import { Box, Button, TextField, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle} from '@material-ui/core';
import { withStyles } from '@material-ui/core/styles'
import PropTypes from 'prop-types'
import Link from '../link'
import { GlobalContext } from '../../../pages/_app'
import { LOGIN_MODAL_STATUS, SIGN_UP_MODAL_STATUS, ALERT_DIALOG, ERROR_TITLE, ERROR_DESCRIPTION, AUTH_STATUS } from '../../lib/utility/type'
import AlertDialog from '../alert'
import { validateEmail } from '../../lib/utility/common'
import request from '../../lib/utility/apiWrapper'

// import { login } from '../../lib/services/login'

const styles = (theme) => ({
    root : {

    },
    textField : {
        marginBottom : 20
    },
    linkWrapper : {
        display : "flex",
        flexDirection : "row",
    },
    linkButton : {
        marginRight : 20
    }
})

const Login = ({ classes, open, handleClose }) => {
  const [ email, setEmail ] = useState(null)
  const [ password, setPassword ] = useState(null)
  const [ errorTitle, setErrorTitle ] = useState(null)
  const [ errorDescription, setErrorDescription ] = useState(null)
  const globalContext = useContext(GlobalContext)

  const onSignUp  = () => {
      globalContext.allDispatch({
        type : SIGN_UP_MODAL_STATUS,
        value : true
      })
      globalContext.allDispatch({
        type : LOGIN_MODAL_STATUS,
        value : false
      })
  }

  const login = async (data) => {
    let loginData = await request({ url : '/userlogin', method : "post", data  }).then((res) => {
      console.log("Here is data ninja",res)
      if(res.result === "success"){
        document.cookie = `token=${res.token}`
        let stringifiedData = JSON.stringify(loginData.data)
        localStorage.setItem('userInfo',stringifiedData)
        globalContext.allDispatch({
          type : AUTH_STATUS,
          value : true
        })
        globalContext.allDispatch({
          type : LOGIN_MODAL_STATUS,
          value : false
        })
      }else if(loginData.result === "wrongEmail"){
        globalContext.allDispatch({
          type : ERROR_DESCRIPTION,
          value : "Please enter correct email."
        })
        globalContext.allDispatch({
          type : ERROR_TITLE,
          value : "Wrong credentials."
        })
        globalContext.allDispatch({
          type : ALERT_DIALOG,
          value : true
        })
      }else if(loginData.result === "wrongPassword"){
        globalContext.allDispatch({
          type : ERROR_DESCRIPTION,
          value : "Please enter correct password."
        })
        globalContext.allDispatch({
          type : ERROR_TITLE,
          value : "Wrong credentials."
        })
        globalContext.allDispatch({
          type : ALERT_DIALOG,
          value : true
        })
      }
    })
  }


  const onSubmit = () => {
    if(email && password){
      if(validateEmail(email)){
        if(password.length > 7){
          let temp = {
            emailOrMobile : email,
            password : password
          }
          login(temp)
        }else{
          errorFunc("Incorrect Password","Minimum password length is 8 charachters.")
        }
      }else{
        errorFunc("Incorrect Email","Please enter correct email and try again.")
      }
    }else{
      errorFunc("Missing Fields","Please enter all mandatory fields.")
    }
  }

  const errorFunc = (title,description) => {
    globalContext.allDispatch({
      type : ERROR_DESCRIPTION,
      value : description
    })
    globalContext.allDispatch({
        type : ERROR_TITLE,
        value : title
    })
    globalContext.allDispatch({
        type : ALERT_DIALOG,
        value : true
    })
  }


  return (
    <div>
      <Dialog open={open} onClose={handleClose} fullWidth={true} maxWidth={'sm'} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Login</DialogTitle>
        <DialogContent>
          <DialogContentText>
          </DialogContentText>
            <Box className={classes.textField}>
              <TextField 
                id="email" 
                label="Email Address"
                type="email"
                fullWidth variant="outlined" 
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </Box>
            <Box className={classes.textField}>
              <TextField 
                id="password" 
                label="Password"
                type="password"
                fullWidth variant="outlined" 
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Box>
            <Box className={classes.linkWrapper}>
                <Box className={classes.linkButton}>
                    <Link href="#" >Forgot Password</Link>
                </Box>
                <Box className={classes.linkButton}>
                    <Link href="#" onClick={onSignUp}>Don't have account! Sign Up</Link>   
                </Box>
            </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={onSubmit} color="primary">
            Submit
          </Button>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
        </DialogActions>
      </Dialog>
      <AlertDialog open={globalContext.state.alertStatus} title={errorTitle} description={errorDescription} />
    </div>
  );
}


Login.propTypes = {
    open : PropTypes.bool,
    handleClose : PropTypes.func,
    // eslint-disable-next-line react/forb,id-prop-types
    classes: PropTypes.object.isRequired,
}

export default withStyles(styles)(Login)
