import React, { useState } from "react";
import ReactDOM from "react-dom";
import { compose, withProps } from "recompose";
import { withGoogleMap, GoogleMap, withScriptjs, InfoWindow, Marker } from "react-google-maps";
import Geocode from "react-geocode";

Geocode.setApiKey("AIzaSyAKXtMAv24joUs0vMvYdOOXIuewWebbCyw");
Geocode.enableDebug();



const MyMapComponent = () => {
    const [mapPosition, setMapPosition] = useState({ lat : 12.9716, lng : 77.5946 })
    const [markerPosition, setMarkerPosition] = useState({ lat : 12.9716, lng : 77.5946 })
    const [address, setAddress] = useState(null)

    const onInfoWindowClose = () => {

    }

    const onMarkerDragEnd = ( event ) => {
		const newLat = event.latLng.lat()
	    const newLng = event.latLng.lng()

		Geocode.fromLatLng( newLat , newLng ).then(
			response => {
                setAddress(response.results[0].formatted_address)
				// const address = response.results[0].formatted_address,
				//       addressArray =  response.results[0].address_components
                    setMapPosition({ lat: newLat, lng: newLng})
                    setMarkerPosition({ lat: newLat, lng: newLng})
			},
			error => {
				console.error(error);
			}
		);
    };

    const onPlaceSelected = ( place ) => {
        console.log( 'plc', place )
        const latValue = place.geometry.location.lat()
		const lngValue = place.geometry.location.lng()
        setAddress(place.formatted_address)
        setMapPosition({ lat: latValue, lng: lngValue})
        setMarkerPosition({ lat: latValue, lng: lngValue})
    }

    const AsyncMap = withScriptjs(
        withGoogleMap(
            props => (
                <GoogleMap 
                    defaultZoom={15}
                    defaultCenter={{ lat: mapPosition.lat, lng: mapPosition.lng }}
                >
                    {/* InfoWindow on top of marker */}
                    <InfoWindow
                        onClose={onInfoWindowClose}
                        position={{ lat: ( markerPosition.lat + 0.0018 ), lng: markerPosition.lng }}
                    >
                        <div>
                            <span style={{ padding: 0, margin: 0 }}>{ address }</span>
                        </div>
                    </InfoWindow>
                    {/*Marker*/}
                    <Marker 
                        name={'Dolores park'}
                        draggable={true}
                        onDragEnd={ onMarkerDragEnd }
                        position={{ lat: markerPosition.lat, lng: markerPosition.lng }}
                    />
                    <Marker />
                    {/* For Auto complete Search Box */}
                    <Autocomplete
                        style={{
                            width: '100%',
                            height: '40px',
                            paddingLeft: '16px',
                            marginTop: '2px',
                            marginBottom: '500px'
                        }}
                        onPlaceSelected={ onPlaceSelected }
                        types={['(regions)']}
                    />
                </GoogleMap>
            )
        )
    )

    return (<AsyncMap
        googleMapURL={`https://maps.googleapis.com/maps/api/js?key=AIzaSyAKXtMAv24joUs0vMvYdOOXIuewWebbCyw&v=3.exp&libraries=geometry,drawing,places`}
        loadingElement={
            <div style={{ height: `100%` }} />
        }
        containerElement={
            <div style={{ height: this.props.height }} />
        }
        mapElement={
            <div style={{ height: `100%` }} />
        }
    />)

}
